// importing express as the comm protocal using api technology
const express = require('express');

// cross origin resource
const cors = require( 'cors' );

// express to make endpoints available
const app = express();
const PORT  = process.env.PORT || 5000;  // Default to 5000 if no PORT environment variable is set
// listens to an available port or default

app.use((req, res, next) => {
    res.setHeader('Content-Type', 'application/json');
    next();
  });  
//.....enabling middleware
app.use(cors());
app.use(express.json());

